/**
 * import all the necessary package
 */
require('dotenv').config();

const morgan = require('morgan');
const express = require('express');
const app = express();

const { PORT } = process.env;
/**
 * Connect to the mongodb database
 */
const { connectMongoDB } = require('./services/database');
connectMongoDB();

/**
 * middlewares
 */
app.use(express.urlencoded({ extended: false }));
app.use(express.json());
app.use(morgan('dev'));

/**
 * Custom routes
 *
 * You can add your custom routes in routes folder
 * Import in this file and use it
 *
 */
const hello = require('./routes/hello')
app.use('/', hello);

/**
 * Listening on the PORT defined by .env file
 */
app.listen(PORT, () => {
	console.log(`Listening on Port ${PORT}`);
});

require('dotenv').config();

const mongoose = require('mongoose');

const { DATABASE } = process.env;

mongoose.set('strictQuery', true);

const connectMongoDB = () => {
	/**
	 * Connecting to mongodb database
	 */
	mongoose
	  .connect('mongodb://127.0.0.1/' + DATABASE)
	  .then(() =>
	    console.log('Successfully connect to ' + DATABASE + ' database')
	)
	  .catch((err) => console.log('Connection err',err));
};

module.exports = { connectMongoDB };

const express = require('express');
const router = express.Router();

router.get('/', (req, res) => {
	res.send('Hello from Node');
});

module.exports = router;
